    // therefore, we have to skip some levels.
    outLoadOptions.levelOffset = avp.LeafletLoader.computeLevelOffset(outLoadOptions.tileSize);

    this.getLeafletZipParams(outLoadOptions, geomItem, 'leaflet-zip');
